@-webkit-keyframes rotation-lr {
  from {
    -webkit-transform: rotate(-90deg);
            transform: rotate(-90deg); }
  to {
    -webkit-transform: rotate(0);
            transform: rotate(0); } }

@keyframes rotation-lr {
  from {
    -webkit-transform: rotate(-90deg);
            transform: rotate(-90deg); }
  to {
    -webkit-transform: rotate(0);
            transform: rotate(0); } }

@-webkit-keyframes rotation-rl {
  from {
    -webkit-transform: rotate(90deg);
            transform: rotate(90deg); }
  to {
    -webkit-transform: rotate(0);
            transform: rotate(0); } }

@keyframes rotation-rl {
  from {
    -webkit-transform: rotate(90deg);
            transform: rotate(90deg); }
  to {
    -webkit-transform: rotate(0);
            transform: rotate(0); } }

@-webkit-keyframes slide-in {
  from {
    opacity: 0;
    -webkit-transform: translateY(10px);
            transform: translateY(10px); }
  to {
    opacity: 1;
    -webkit-transform: translateY(0);
            transform: translateY(0); } }

@keyframes slide-in {
  from {
    opacity: 0;
    -webkit-transform: translateY(10px);
            transform: translateY(10px); }
  to {
    opacity: 1;
    -webkit-transform: translateY(0);
            transform: translateY(0); } }

@-webkit-keyframes slide-out {
  from {
    opacity: 1;
    -webkit-transform: translateY(0);
            transform: translateY(0); }
  to {
    opacity: 0;
    -webkit-transform: translateY(10px);
            transform: translateY(10px); } }

@keyframes slide-out {
  from {
    opacity: 1;
    -webkit-transform: translateY(0);
            transform: translateY(0); }
  to {
    opacity: 0;
    -webkit-transform: translateY(10px);
            transform: translateY(10px); } }

/**
 * Dots Animations - Adding new animations types is just adding a scss file to
 * the animations folder. Then calling the Loader component with the new type name
 */
@-webkit-keyframes letstalk-ball-beat {
  50% {
    -webkit-transform: scale(0.75);
            transform: scale(0.75);
    opacity: 0.2; }
  100% {
    -webkit-transform: scale(1);
            transform: scale(1);
    opacity: 1; } }
@keyframes letstalk-ball-beat {
  50% {
    -webkit-transform: scale(0.75);
            transform: scale(0.75);
    opacity: 0.2; }
  100% {
    -webkit-transform: scale(1);
            transform: scale(1);
    opacity: 1; } }

.letstalk-ball-beat > div {
  margin: 2px;
  background-color: black;
  -webkit-animation-fill-mode: both;
          animation-fill-mode: both;
  display: inline-block;
  border-radius: 100%;
  -webkit-animation: letstalk-ball-beat 2s 1s infinite linear;
          animation: letstalk-ball-beat 2s 1s infinite linear; }
  .letstalk-ball-beat > div:nth-child(2n-1) {
    -webkit-animation-delay: 1s !important;
            animation-delay: 1s !important; }

.letstalk-ball-beat.xsmall,
.letstalk-ball-beat.xsmall > div {
  min-width: 6.4px;
  min-height: 6.4px;
  border-width: 0.4px; }

.letstalk-ball-beat.small,
.letstalk-ball-beat.small > div {
  min-width: 16px;
  min-height: 16px;
  border-width: 1px; }

.letstalk-ball-beat.medium,
.letstalk-ball-beat.medium > div {
  min-width: 24px;
  min-height: 24px;
  border-width: 1.5px; }

.letstalk-ball-beat.large,
