  min-width: 40px;
  min-height: 40px;
  border-width: 2.5px; }

.letstalk-ball-beat.xlarge,
.letstalk-ball-beat.xlarge > div {
  min-width: 64px;
  min-height: 64px;
  border-width: 4px; }

@-webkit-keyframes letstalk-loader-scale {
  0% {
    -webkit-transform: scale(0);
            transform: scale(0);
    opacity: 0.2;
    -webkit-animation-timing-function: ease-out;
            animation-timing-function: ease-out; }
  30% {
    -webkit-transform: scale(1);
            transform: scale(1);
    opacity: 0.8;
    -webkit-animation-timing-function: ease-in;
            animation-timing-function: ease-in; }
