.letstalk-ball-pulse-sync > div:nth-child(1) {
  -webkit-animation: letstalk-ball-pulse-sync 2s -0.14s infinite ease-in-out;
          animation: letstalk-ball-pulse-sync 2s -0.14s infinite ease-in-out; }

.letstalk-ball-pulse-sync > div:nth-child(2) {
  -webkit-animation: letstalk-ball-pulse-sync 2s -0.07s infinite ease-in-out;
          animation: letstalk-ball-pulse-sync 2s -0.07s infinite ease-in-out; }

.letstalk-ball-pulse-sync > div:nth-child(3) {
